import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TransactionList.css';

function TransactionList() {
    const [transactions, setTransactions] = useState([]);
    const [categories, setCategories] = useState([]);
    const [sousCategories, setSousCategories] = useState([]);
    const [categorie, setCategorie] = useState('');
    const [sousCategorie, setSousCategorie] = useState('');
    const [date, setDate] = useState('');
    const [triMontant, setTriMontant] = useState('');

    // Récupération des catégories et sous-catégories
    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
            setCategories(response.data.categories || []);
            setSousCategories(response.data.sousCategories || []);
        } catch (error) {
            console.error("Erreur lors de la récupération des catégories :", error);
        }
    };

    // Récupération des transactions
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                params: {
                    categorie,
                    sous_categorie: sousCategorie,
                    date,
                    tri_montant: triMontant
                }
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération des transactions :", error);
        }
    };

    // Suppression d'une transaction
    const deleteTransaction = async (id) => {
        try {
            const response = await axios.delete('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                data: { id }, // Passe l'ID dans le corps de la requête
                headers: { 'Content-Type': 'application/json' }, // Définit le type de contenu comme JSON
            });

            if (response.data.status === 'success') {
                setTransactions(transactions.filter((transaction) => transaction.id !== id));
                alert('Transaction supprimée avec succès.');
            } else {
                console.error("Erreur API :", response.data.message);
            }
        } catch (error) {
            console.error("Erreur lors de la suppression de la transaction :", error);
        }
    };

    useEffect(() => {
        fetchCategories();
        fetchTransactions();
    }, []);

    return (
        <div className="transaction-list-container">
            <h2 className="transaction-title">Transactions</h2>
            <ul className="transaction-list">
                {transactions.map((transaction) => (
                    <li key={transaction.id} className="transaction-item">
                        <span className="transaction-date">{transaction.date}</span>
                        <span className="transaction-title">{transaction.titre}</span>
                        <span className="transaction-amount">{Number(transaction.montant).toFixed(2)} €</span>
                        <button className="delete-button" onClick={() => deleteTransaction(transaction.id)}>Supprimer</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TransactionList;
