import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TransactionList.css';

function TransactionList() {
    const [transactions, setTransactions] = useState([]);
    const [editTransaction, setEditTransaction] = useState(null); // Pour gérer la modification
    const [categories, setCategories] = useState([]);
    const [sousCategories, setSousCategories] = useState([]);
    const [categorie, setCategorie] = useState('');
    const [sousCategorie, setSousCategorie] = useState('');
    const [date, setDate] = useState('');
    const [triMontant, setTriMontant] = useState('');

    // Fonction pour récupérer les catégories et sous-catégories
    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
            setCategories(response.data.categories || []);
            setSousCategories(response.data.sousCategories || []);
        } catch (error) {
            console.error("Erreur lors de la récupération des catégories :", error);
        }
    };

    // Fonction pour récupérer les transactions
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                params: {
                    categorie,
                    sous_categorie: sousCategorie,
                    date,
                    tri_montant: triMontant
                }
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération des transactions :", error);
        }
    };

    // Fonction pour supprimer une transaction
    const deleteTransaction = async (id) => {
        try {
            await axios.delete(`http://localhost:8888/finance-flow/finance-flow/src/api.php?id=${id}`);
            fetchTransactions(); // Rafraîchir les transactions
        } catch (error) {
            console.error("Erreur lors de la suppression :", error);
        }
    };

    // Fonction pour sauvegarder une transaction modifiée
    const saveTransaction = async (transaction) => {
        try {
            await axios.put('http://localhost:8888/finance-flow/finance-flow/src/api.php', transaction);
            setEditTransaction(null); // Fermer le formulaire de modification
            fetchTransactions();
        } catch (error) {
            console.error("Erreur lors de la modification :", error);
        }
    };

    useEffect(() => {
        fetchCategories();
        fetchTransactions();
    }, []);

    return (
        <div className="transaction-list-container">
            <h2 className="transaction-title">Transactions</h2>

            <ul className="transaction-list">
                {transactions.map((transaction) => (
                    <li key={transaction.id} className="transaction-item">
                        <span className="transaction-date">{transaction.date}</span>
                        <span className="transaction-title">{transaction.titre}</span>
                        <span className="transaction-amount">{transaction.montant.toFixed(2)} €</span>
                        <div className="transaction-actions">
                            <button
                                className="edit-button"
                                onClick={() => setEditTransaction(transaction)}
                            >
                                Modifier
                            </button>
                            <button
                                className="delete-button"
                                onClick={() => deleteTransaction(transaction.id)}
                            >
                                Supprimer
                            </button>
                        </div>
                    </li>
                ))}
            </ul>

            {editTransaction && (
                <div className="edit-form">
                    <h3>Modifier la Transaction</h3>
                    <form
                        onSubmit={(e) => {
                            e.preventDefault();
                            saveTransaction(editTransaction);
                        }}
                    >
                        <input
                            type="text"
                            value={editTransaction.titre}
                            onChange={(e) =>
                                setEditTransaction({ ...editTransaction, titre: e.target.value })
                            }
                            required
                        />
                        <input
                            type="number"
                            value={editTransaction.montant}
                            onChange={(e) =>
                                setEditTransaction({
                                    ...editTransaction,
                                    montant: parseFloat(e.target.value),
                                })
                            }
                            required
                        />
                        <button type="submit">Sauvegarder</button>
                        <button type="button" onClick={() => setEditTransaction(null)}>
                            Annuler
                        </button>
                    </form>
                </div>
            )}
        </div>
    );
}

export default TransactionList;
