import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TransactionList() {
    const [transactions, setTransactions] = useState([]);
    const [categories, setCategories] = useState([]);
    const [selectedTransaction, setSelectedTransaction] = useState(null);
    const [utilisateurs, setUtilisateurs] = useState([]);
    const [selectedUser, setSelectedUser] = useState('');

    // Récupérer les transactions
    useEffect(() => {
        const fetchTransactions = async () => {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php');
            setTransactions(response.data);
        };

        const fetchCategories = async () => {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
            setCategories(response.data.categories || []);
        };

        const fetchUtilisateurs = async () => {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getUtilisateurs=true');
            setUtilisateurs(response.data || []);
        };

        fetchTransactions();
        fetchCategories();
        fetchUtilisateurs();
    }, []);

    // Partager une transaction
    const handleShareTransaction = async () => {
        if (selectedTransaction && selectedUser) {
            await axios.post('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                shareTransaction: true,
                transaction_id: selectedTransaction,
                utilisateur_id: selectedUser
            });
            alert("Transaction partagée avec succès !");
            setSelectedTransaction(null);
            setSelectedUser('');
        }
    };

    return (
        <div>
            <h2>Transactions</h2>
            <ul>
                {transactions.map((transaction) => (
                    <li key={transaction.id}>
                        {transaction.date} - {transaction.titre} : {transaction.montant}€
                        <button onClick={() => setSelectedTransaction(transaction.id)}>Partager</button>
                    </li>
                ))}
            </ul>

            {selectedTransaction && (
                <div>
                    <h3>Partager la transaction</h3>
                    <label>Utilisateur :</label>
                    <select value={selectedUser} onChange={(e) => setSelectedUser(e.target.value)}>
                        <option value="">Sélectionner un utilisateur</option>
                        {utilisateurs.map(user => (
                            <option key={user.id} value={user.id}>{user.nom}</option>
                        ))}
                    </select>
                    <button onClick={handleShareTransaction}>Partager</button>
                </div>
            )}
        </div>
    );
}

export default TransactionList;
