import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TransactionForm from './components/TransactionForm';
import TransactionList from './components/TransactionList';
import Solde from './components/Solde';
import './App.css';

function App() {
    const [transactions, setTransactions] = useState([]);
    const [solde, setSolde] = useState(0);

    // Fonction pour récupérer les transactions depuis l'API
    const fetchTransactions = async () => {
        try {
            const response = await axios.get('http://localhost/finance-flow/api.php');
            setTransactions(response.data);
            calculateSolde(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération des transactions :", error);
        }
    };

    // Calculer le solde basé sur les transactions
    const calculateSolde = (transactions) => {
        const total = transactions.reduce((acc, tr) => acc + (tr.type === 'revenu' ? tr.montant : -tr.montant), 0);
        setSolde(total);
    };

    // Fonction pour gérer l'ajout d'une nouvelle transaction
    const handleAddTransaction = async (transaction) => {
        try {
            const response = await axios.post('http://localhost/finance-flow/api.php', transaction);
            if (response.data.status === 'success') {
                fetchTransactions();  // Mise à jour des transactions après ajout
            } else {
                alert(response.data.message);  // Affiche un message d'erreur si l'API en renvoie un
            }
        } catch (error) {
            console.error("Erreur lors de l'ajout de la transaction :", error);
        }
    };

    // Charger les transactions au montage du composant
    useEffect(() => {
        fetchTransactions();
    }, []);

    return (
        <div className="container">
            <header>
                <h1>Mon Gestionnaire de Budget</h1>
            </header>
            <Solde solde={solde} />
            <TransactionForm onAddTransaction={handleAddTransaction} />
            <TransactionList transactions={transactions} />
        </div>
    );
}

export default App;
