import React, { useState } from 'react';

function TransactionForm({ onAddTransaction }) {
  const [titre, setTitre] = useState('');
  const [montant, setMontant] = useState('');
  const [type, setType] = useState('depense');
  const [date, setDate] = useState('');
  const [lieu, setLieu] = useState('');
  const [description, setDescription] = useState('');
  const [categorie, setCategorie] = useState('');
  const [sousCategorie, setSousCategorie] = useState('');
  const [utilisateur, setUtilisateur] = useState(''); // Nouveau champ pour le partage

  const handleSubmit = (e) => {
    e.preventDefault();
    const transaction = { 
        titre, 
        montant: parseFloat(montant), 
        type, 
        date, 
        lieu, 
        description, 
        categorie_id: parseInt(categorie), 
        sous_categorie_id: parseInt(sousCategorie),
        utilisateur // Ajout de l'utilisateur pour le partage
    };
    onAddTransaction(transaction);
    setTitre('');
    setMontant('');
    setType('depense');
    setDate('');
    setLieu('');
    setDescription('');
    setCategorie('');
    setSousCategorie('');
    setUtilisateur(''); // Réinitialise le champ utilisateur
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={titre} onChange={(e) => setTitre(e.target.value)} placeholder="Titre" required />
      <input type="number" value={montant} onChange={(e) => setMontant(e.target.value)} placeholder="Montant" required />
      <select value={type} onChange={(e) => setType(e.target.value)}>
        <option value="depense">Dépense</option>
        <option value="revenu">Revenu</option>
      </select>
      <input type="date" value={date} onChange={(e) => setDate(e.target.value)} required />
      <input type="text" value={lieu} onChange={(e) => setLieu(e.target.value)} placeholder="Lieu" />
      <textarea value={description} onChange={(e) => setDescription(e.target.value)} placeholder="Description"></textarea>
      <input type="text" value={categorie} onChange={(e) => setCategorie(e.target.value)} placeholder="Catégorie" required />
      <input type="text" value={sousCategorie} onChange={(e) => setSousCategorie(e.target.value)} placeholder="Sous-catégorie" required />
      <input type="text" value={utilisateur} onChange={(e) => setUtilisateur(e.target.value)} placeholder="Utilisateur" required />
      <button type="submit">Ajouter</button>
    </form>
  );
}

export default TransactionForm;
