import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BudgetForm.css';

function BudgetForm() {
    const [categories, setCategories] = useState([]);
    const [budgets, setBudgets] = useState({});

    useEffect(() => {
        // Charger les catégories et budgets
        const fetchData = async () => {
            try {
                const categoriesResponse = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
                setCategories(categoriesResponse.data.categories || []);

                const budgetsResponse = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getBudgets=true');
                const budgetsData = budgetsResponse.data.reduce((acc, { categorie_id, budget }) => {
                    acc[categorie_id] = budget;
                    return acc;
                }, {});
                setBudgets(budgetsData);
            } catch (error) {
                console.error("Erreur lors de la récupération des données :", error);
            }
        };

        fetchData();
    }, []);

    const handleBudgetChange = (categoryId, amount) => {
        axios.post('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
            categoryId,
            budget: parseFloat(amount)
        })
        .then((response) => {
            if (response.data.status === 'success') {
                setBudgets({ ...budgets, [categoryId]: parseFloat(amount) });
                console.log("Budget mis à jour avec succès");
            } else {
                console.error("Erreur lors de la mise à jour :", response.data.message);
            }
        })
        .catch(error => console.error("Erreur lors de la mise à jour du budget :", error));
    };

    return (
        <div className="budget-form-container">
            <h2>Définir le budget par catégorie</h2>
            <form className="budget-form">
                {categories.map((category) => (
                    <div key={category.id} className="budget-item">
                        <label>{category.nom} :</label>
                        <input
                            type="number"
                            placeholder="Budget"
                            value={budgets[category.id] || ''}
                            onChange={(e) => handleBudgetChange(category.id, e.target.value)}
                        />
                    </div>
                ))}
            </form>
        </div>
    );
}

export default BudgetForm;
