import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BudgetForm.css';

function BudgetForm() {
    const [categories, setCategories] = useState([]);
    const [budgets, setBudgets] = useState({});

    useEffect(() => {
        // Charger les catégories et budgets
        axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true')
            .then(response => {
                setCategories(response.data.categories || []);
                return axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getBudgets=true');
            })
            .then(budgetResponse => {
                // Créer un objet budgets par catégorie
                const budgetData = budgetResponse.data.reduce((acc, { categorie_id, budget }) => {
                    acc[categorie_id] = budget;
                    return acc;
                }, {});
                setBudgets(budgetData);
            })
            .catch(error => console.error("Erreur lors de la récupération des données :", error));
    }, []);

    const handleBudgetChange = (categoryId, amount) => {
        setBudgets(prevBudgets => ({
            ...prevBudgets,
            [categoryId]: parseFloat(amount) || 0
        }));
    };

    const saveBudgets = () => {
        const requests = Object.entries(budgets).map(([categoryId, budget]) =>
            axios.post('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                categoryId,
                budget
            })
        );

        Promise.all(requests)
            .then(() => {
                console.log("Tous les budgets ont été enregistrés avec succès");
                alert("Budgets enregistrés avec succès !");
            })
            .catch(error => console.error("Erreur lors de la sauvegarde des budgets :", error));
    };

    return (
        <div className="budget-form-container">
            <div className="budget-form-header">Définir le budget par catégorie</div>
            <form className="budget-form" onSubmit={(e) => e.preventDefault()}>
                {categories.map(category => (
                    <div key={category.id} className="budget-item">
                        <label>{category.nom}:</label>
                        <input
                            type="number"
                            placeholder="Budget"
                            defaultValue={budgets[category.id] || 0}
                            onChange={(e) => handleBudgetChange(category.id, e.target.value)}
                        />
                    </div>
                ))}
                <button
                    type="button"
                    className="budget-submit-btn"
                    onClick={saveBudgets}
                >
                    Enregistrer les Budgets
                </button>
            </form>
        </div>
    );
}

export default BudgetForm;
