import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TransactionList.css';

function TransactionList() {
    const [transactions, setTransactions] = useState([]);
    const [categorie, setCategorie] = useState('');
    const [sousCategorie, setSousCategorie] = useState('');
    const [date, setDate] = useState('');
    const [triMontant, setTriMontant] = useState('');
    const [categories, setCategories] = useState([]);
    const [sousCategories, setSousCategories] = useState([]);

    // Fonction pour récupérer les catégories et sous-catégories
    const fetchCategories = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
            setCategories(response.data.categories || []);
            setSousCategories(response.data.sousCategories || []);
        } catch (error) {
            console.error("Erreur lors de la récupération des catégories :", error);
        }
    };

    // Fonction pour appliquer les filtres
    const appliquerFiltres = async () => {
        try {
            const response = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                params: {
                    categorie,
                    sous_categorie: sousCategorie,
                    date,
                    tri_montant: triMontant
                }
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération des transactions :", error);
        }
    };

    // Charger les catégories et les transactions au chargement du composant
    useEffect(() => {
        fetchCategories();
        appliquerFiltres();
    }, []);

    // Appliquer les filtres quand les filtres changent
    useEffect(() => {
        appliquerFiltres();
    }, [categorie, sousCategorie, date, triMontant]);

    return (
        <div className="transaction-list-container">
            <h2 className="title">Filtres et Tri</h2>
            <form className="filter-form">
                <div className="form-group">
                    <label>Catégorie :</label>
                    <select value={categorie} onChange={(e) => setCategorie(e.target.value)}>
                        <option value="">Toutes</option>
                        {categories.map((cat) => (
                            <option key={cat.id} value={cat.id}>{cat.nom}</option>
                        ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Sous-catégorie :</label>
                    <select value={sousCategorie} onChange={(e) => setSousCategorie(e.target.value)}>
                        <option value="">Toutes</option>
                        {sousCategories
                            .filter((subCat) => subCat.categorie_id === parseInt(categorie))
                            .map((subCat) => (
                                <option key={subCat.id} value={subCat.id}>{subCat.nom}</option>
                            ))}
                    </select>
                </div>

                <div className="form-group">
                    <label>Date :</label>
                    <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
                </div>

                <div className="form-group">
                    <label>Trier par montant :</label>
                    <select value={triMontant} onChange={(e) => setTriMontant(e.target.value)}>
                        <option value="">Aucun</option>
                        <option value="asc">Croissant</option>
                        <option value="desc">Décroissant</option>
                    </select>
                </div>

                <button type="button" className="filter-button" onClick={appliquerFiltres}>Appliquer les Filtres</button>
            </form>

            <h2 className="title">Transactions</h2>
            <ul className="transaction-list">
                {transactions.map((transaction) => (
                    <li key={transaction.id} className="transaction-item">
                        <span>{transaction.date}</span>
                        <span>{transaction.titre}</span>
                        <span>{transaction.montant}€</span>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TransactionList;
