import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BudgetForm.css';

function BudgetForm() {
    const [categories, setCategories] = useState([]);
    const [budgets, setBudgets] = useState({});
    const [loading, setLoading] = useState(false);

    // Récupérer les catégories et budgets
    useEffect(() => {
        const fetchData = async () => {
            try {
                setLoading(true);
                const categoriesResponse = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true');
                setCategories(categoriesResponse.data.categories || []);

                const budgetsResponse = await axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getBudgets=true');
                setBudgets(
                    budgetsResponse.data.reduce((acc, { categorie_id, budget }) => ({
                        ...acc,
                        [categorie_id]: parseFloat(budget),
                    }), {})
                );
            } catch (error) {
                console.error("Erreur lors de la récupération des données :", error);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    // Mettre à jour ou ajouter un budget
    const handleBudgetChange = async (categoryId, amount) => {
        try {
            await axios.post('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                action: 'updateBudget',
                categoryId,
                budget: parseFloat(amount) || 0,
            });
            alert("Budget mis à jour avec succès !");
            setBudgets((prev) => ({
                ...prev,
                [categoryId]: parseFloat(amount),
            }));
        } catch (error) {
            console.error("Erreur lors de la mise à jour du budget :", error);
        }
    };

    return (
        <div className="budget-form-container">
            <div className="budget-form-header">Définir le budget par catégorie</div>
            {loading ? (
                <p>Chargement...</p>
            ) : (
                <form className="budget-form">
                    {categories.map((category) => (
                        <div key={category.id} className="budget-item">
                            <label>{category.nom}:</label>
                            <input
                                type="number"
                                placeholder="Budget"
                                value={budgets[category.id] || ''}
                                onChange={(e) => handleBudgetChange(category.id, e.target.value)}
                            />
                        </div>
                    ))}
                </form>
            )}
        </div>
    );
}

export default BudgetForm;
