import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TransactionList() {
    const [transactions, setTransactions] = useState([]);
    const [filters, setFilters] = useState({
        categorie: '',
        sous_categorie: '',
        date: '',
        tri_montant: ''
    });

    const appliquerFiltres = async () => {
        try {
            const { categorie, sous_categorie, date, tri_montant } = filters;
            const response = await axios.get(`http://localhost:8888/finance-flow/finance-flow/src/api.php`, {
                params: { categorie, sous_categorie, date, tri_montant }
            });
            setTransactions(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération des transactions :", error);
        }
    };

    useEffect(() => {
        appliquerFiltres();
    }, [filters]);

    const handleChange = (e) => {
        setFilters({ ...filters, [e.target.name]: e.target.value });
    };

    return (
        <div>
            <h2>Liste des transactions</h2>
            <button onClick={appliquerFiltres}>Appliquer les filtres</button>
            <ul>
                {transactions.map((transaction) => (
                    <li key={transaction.id}>{transaction.titre} - {transaction.montant}</li>
                ))}
            </ul>
        </div>
    );
}

export default TransactionList;
