import React, { useState, useEffect } from 'react';
import axios from 'axios';

function TransactionList({ transactions, onFilterChange }) {
    const [categories, setCategories] = useState([]);
    const [sousCategories, setSousCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [selectedSousCategorie, setSelectedSousCategorie] = useState('');

    // Charger les catégories et sous-catégories
    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await axios.get('http://localhost:8888/finance-flow/api.php?getCategories=true');
                setCategories(response.data.categories || []);
                setSousCategories(response.data.sousCategories || []);
            } catch (error) {
                console.error("Erreur lors de la récupération des catégories :", error);
            }
        };
        fetchCategories();
    }, []);

    // Filtrer les sous-catégories en fonction de la catégorie sélectionnée
    const filteredSousCategories = sousCategories.filter(
        (sousCategorie) => sousCategorie.categorie_id === parseInt(selectedCategory)
    );

    // Gérer les changements de filtre
    const handleCategoryChange = (e) => {
        const selected = e.target.value;
        setSelectedCategory(selected);
        onFilterChange({ categorie: selected, sous_categorie: selectedSousCategorie });
    };

    const handleSousCategoryChange = (e) => {
        const selected = e.target.value;
        setSelectedSousCategorie(selected);
        onFilterChange({ categorie: selectedCategory, sous_categorie: selected });
    };

    return (
        <div>
            <label>Catégorie :</label>
            <select value={selectedCategory} onChange={handleCategoryChange}>
                <option value="">Toutes les catégories</option>
                {categories.map((categorie) => (
                    <option key={categorie.id} value={categorie.id}>{categorie.nom}</option>
                ))}
            </select>

            <label>Sous-catégorie :</label>
            <select value={selectedSousCategorie} onChange={handleSousCategoryChange}>
                <option value="">Toutes les sous-catégories</option>
                {filteredSousCategories.map((sousCategorie) => (
                    <option key={sousCategorie.id} value={sousCategorie.id}>{sousCategorie.nom}</option>
                ))}
            </select>
        </div>
    );
}

export default TransactionList;
