import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './BudgetForm.css';

function BudgetForm() {
    const [categories, setCategories] = useState([]);
    const [budgets, setBudgets] = useState({});

    // Charger les catégories et budgets existants
    useEffect(() => {
        axios
            .get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getCategories=true')
            .then((response) => {
                setCategories(response.data.categories || []);
                return axios.get('http://localhost:8888/finance-flow/finance-flow/src/api.php?getBudgets=true');
            })
            .then((budgetResponse) => {
                const budgetMap = budgetResponse.data.reduce(
                    (acc, { categorie_id, budget }) => ({
                        ...acc,
                        [categorie_id]: budget,
                    }),
                    {}
                );
                setBudgets(budgetMap);
            })
            .catch((error) => console.error('Erreur lors de la récupération des données :', error));
    }, []);

    // Mettre à jour le budget dans la base de données
    const handleBudgetChange = (categoryId, amount) => {
        axios
            .post('http://localhost:8888/finance-flow/finance-flow/src/api.php', {
                action: 'updateBudget',
                categoryId: categoryId,
                budget: amount,
            })
            .then(() => {
                alert('Budget mis à jour avec succès');
                setBudgets((prevBudgets) => ({ ...prevBudgets, [categoryId]: amount }));
            })
            .catch((error) => console.error('Erreur lors de la mise à jour du budget :', error));
    };

    return (
        <div className="budget-form-container">
            <div className="budget-form-header">Définir le budget par catégorie</div>
            <form className="budget-form">
                {categories.map((category) => (
                    <div key={category.id} className="budget-item">
                        <label>{category.nom} :</label>
                        <input
                            type="number"
                            placeholder="Budget"
                            defaultValue={budgets[category.id] || 0}
                            onBlur={(e) => handleBudgetChange(category.id, parseFloat(e.target.value))}
                        />
                    </div>
                ))}
                <button type="button" className="budget-submit-btn">
                    Enregistrer les Budgets
                </button>
            </form>
        </div>
    );
}

export default BudgetForm;
